// html2canvas - CDN Loader
// For offline use: Replace this file with the library from https://html2canvas.hertzen.com/
// Download: https://cdnjs.cloudflare.com/ajax/libs/html2canvas/1.4.1/html2canvas.min.js

(function() {
    // Check if already loaded
    if (typeof html2canvas !== 'undefined') {
        return;
    }
    
    // Create promise for this library
    window.libraryLoader = window.libraryLoader || {};
    window.libraryLoader.html2canvas = new Promise((resolve, reject) => {
        const script = document.createElement('script');
        script.src = 'https://cdnjs.cloudflare.com/ajax/libs/html2canvas/1.4.1/html2canvas.min.js';
        script.onload = () => {
            console.log('html2canvas loaded successfully');
            resolve();
        };
        script.onerror = () => {
            console.error('Failed to load html2canvas. For offline use, download the library from https://html2canvas.hertzen.com/');
            reject(new Error('Failed to load html2canvas'));
        };
        document.head.appendChild(script);
    });
})();
