// jStat Statistical Computing Library - CDN Loader
// For offline use: Replace this file with the library from https://jstat.github.io/
// Download: https://cdn.jsdelivr.net/npm/jstat@1.9.6/dist/jstat.min.js

(function() {
    // Check if already loaded
    if (typeof jStat !== 'undefined') {
        return;
    }
    
    // Create promise for this library
    window.libraryLoader = window.libraryLoader || {};
    window.libraryLoader.jstat = new Promise((resolve, reject) => {
        const script = document.createElement('script');
        script.src = 'https://cdn.jsdelivr.net/npm/jstat@1.9.6/dist/jstat.min.js';
        script.onload = () => {
            console.log('jStat loaded successfully');
            resolve();
        };
        script.onerror = () => {
            console.error('Failed to load jStat. For offline use, download the library from https://jstat.github.io/');
            reject(new Error('Failed to load jStat'));
        };
        document.head.appendChild(script);
    });
})();
