(function() {
    // Load Simple Statistics from CDN
    const script = document.createElement('script');
    script.src = 'https://unpkg.com/simple-statistics@7.8.3/dist/simple-statistics.min.js';
    script.onerror = function() {
        console.error('Failed to load Simple Statistics. For offline use, download the library from https://github.com/simple-statistics/simple-statistics');
        alert('Simple Statistics library failed to load. Check your internet connection or download the library for offline use.');
    };
    document.head.appendChild(script);
})();
